mapper.select:
SELECT * FROM `shop` WHERE `del` = 1
AND `name` like CONCAT('%',#{name},'%')
AND substr(`type`, 1, 2) = #{type}
AND `area` = #{area}

SELECT
  a.*
FROM
  (SELECT
    *,
    ROUND(
      6378.138 * 2 * ASIN(
        SQRT(
          POW(
            SIN(
              (
                #{latitude} * PI() / 180 - latitude * PI() / 180
              ) / 2
            ),
            2
            ) + COS(#{latitude} * PI() / 180) * COS(latitude * PI() / 180) * POW(
            SIN(
              (
                #{longitude} * PI() / 180 - longitude * PI() / 180
              ) / 2
            ),
            2
          )
        )
      ) * 1000
    )/1000 AS distance
  FROM
    `shop`)a
WHERE
  a.del = 1

    AND a.name like CONCAT('%',#{name},'%')


    AND substr(a.type, 1, 2) = #{type}


    AND #{distance} >= a.distance


    AND a.area = #{area}

ORDER BY
  a.distance ASC
